INSERT INTO `doctor` VALUES (10001, 2, '孟照华', 'abcd1234', '24.jpg', '糖尿病', 2, '内科常见病，多发病的诊断和治疗。如高血压，糖尿病，高尿酸血症，高血脂症，哮喘等诊断和治疗上具有丰富的临床经验。', '毕业于吉林医学院临床医疗系，从事内科临床工作30余年，具有丰富的临床经验，擅长高血压，糖尿病，高脂血症，等内科常见疾病的诊断和治疗。', '南京市第一医院', 4.5, 0.91, 1127, 4.6);
INSERT INTO `doctor` VALUES (10002, 2, '欧阳后华', 'abcd1234', '28.jpg', '糖尿病', 2, '擅长2型和1型糖尿病的综合治疗，特别擅长难治性糖尿病及严重并发症的个性化治疗。', '2002年7月广西医科大学本科毕业 2013年7月广西医科大学在职研究生毕业', '钦州市第二人民医院', 4.3, 0.89, 234, 3.9);
INSERT INTO `doctor` VALUES (10003, 1, '谢逸群', 'abcd1234', '01.jpg', '糖尿病', 4, '糖尿病筛查（包括单基因突变型糖尿病的诊断）、胰岛功能评估，并发症及合并症的防治，擅长中老年人垂体疾病引发的内分泌糖尿病。', '湘雅求学，内科2型和1型糖尿病临床医生，内科临床医学博士。', '中南大学湘雅二医院', 4.9, 0.98, 745, 4.3);
INSERT INTO `doctor` VALUES (10004, 1, '贺慧鹏', 'abcd1234', '02.jpg', '糖尿病', 1, '擅长内分泌科疾病的诊治，特别是对糖尿病及胰腺疾病的诊治有丰富的临床经验。', '毕业于中南大学湘雅医学院，临床医学硕士，副主任医师。', '株洲市中心医院', 3.8, 0.86, 348, 4);
INSERT INTO `doctor` VALUES (10005, 1, '何林海', 'abcd1234', '03.jpg', '糖尿病', 1, '擅长治疗内分泌代谢系统疾病的诊断和治疗，特别是老年性1型糖尿病免疫干预治疗。', '毕业于湖南省中南大学湘雅医学院的内科临床硕士研究生，在湘雅附二学习三年，毕业后在市中心医院工作2年。', '邵阳市中心医院', 3.4, 0.81, 861, 4.5);
INSERT INTO `doctor` VALUES (10006, 2, '彭娟', 'abcd1234', '32.jpg', '糖尿病', 3, '擅长用中西医结合方法治疗内分泌糖尿疾病。', '西南医科大学内分泌科研究生毕业。', '西南医科大学附属中医医院', 4.7, 0.95, 114, 3.2);
INSERT INTO `doctor` VALUES (10007, 1, '李定刚', 'abcd1234', '04.jpg', '糖尿病', 2, '就医于内江市第二人民医院康复二科，擅长于内分泌疾病的诊断治疗，擅长于高血压病、冠心病、糖尿病等疾病的膳食调理。', '2005-2010年就学于成都中医药大学内分泌学专业，毕业后就医于内江市第二人民医院内分泌科至今。', '内江市第二人民医院', 4.6, 0.93, 674, 4.3);
INSERT INTO `doctor` VALUES (10008, 1, '江训盛', 'abcd1234', '05.jpg', '糖尿病', 2, '内分泌专业疑难杂症，尤擅长糖尿病及其并发症如糖尿病肾病、糖尿病足及心血管病的诊断。对老年性糖尿病的鉴别和诊治有独到造诣。', '内分泌医学硕士毕业，擅长难治性糖尿病及严重并发症的诊治，尤其是孕产期糖尿病及甲状腺病的个体化治疗。', '南昌市第三医院', 4.4, 0.91, 235, 3.8);
INSERT INTO `doctor` VALUES (10009, 2, '周娲', 'abcd1234', '34.jpg', '高血压', 2, '各种糖尿病及慢性并发症。', '毕业于南昌大学医院院研究生院，在上海进修学习', '南昌市第三医院', 4.3, 0.9, 156, 3.5);
INSERT INTO `doctor` VALUES (10010, 2, '齐淑文', 'abcd1234', '35.jpg', '高血压', 2, '高血压管理，治疗，饮食运动疗法，心脑等高血压，脑血栓，心梗等慢病预防与治疗。全科方面如呼吸道，消化道，泌尿系疾病，常见心理疾患的诊疗。', '本科是江西医学院临床医学系毕业，后河北医科大学硕士研究生毕业，在南大一附院内科进修一年。', '武警江西总医院', 4.2, 0.88, 784, 4.3);
INSERT INTO `doctor` VALUES (10011, 1, '吴清国', 'abcd1234', '06.jpg', '高血压', 1, '从事心血管内科13年，从事冠心病、高血压病、心力衰竭、心律失常等心血管疾病治疗。擅长心电生理+射频消融术及起搏器植入手术治疗。', '2000至2005就读福建医科大学临床医学专业，毕业后于莆田市第一医院心血管内科工作。2014至2015于福建省立医院导管室培训心电生理射频消融术。', '莆田市第一医院', 3.8, 0.84, 348, 4.1);
INSERT INTO `doctor` VALUES (10012, 1, '黄智伟', 'abcd1234', '07.jpg', '高血压', 1, '冠心病 心肌梗死 高血压病 高脂血症 心律失常（早搏 房颤）先天性心脏病 心力衰竭 肺动脉高压 肺栓塞等', '医学博士，毕业于北京协和医学院，中国医学科学院阜外医院，国家心血管病中心。', '阜外心血管医院', 3.6, 0.81, 964, 4.6);
INSERT INTO `doctor` VALUES (10013, 1, '王非', 'abcd1234', '08.jpg', '高血压', 2, '擅长常见心血管内科疾病的诊疗，例如冠心病，高血压，心衰等，以及生活谣言甄别、医疗健康咨询。', '第三军医大学心血管内科博士毕业，从事一线临床工作10余年，曾于火箭军总医院进修。', '解放军第五三二医院', 4.5, 0.93, 157, 2.9);
INSERT INTO `doctor` VALUES (10014, 2, '蒋红双', 'abcd1234', '38.jpg', '高血压', 4, '提供高血压、冠心病、糖尿病、脑梗等心脑血管疾病的专业的用药指导，以及健康教育的宣教。', '2013年毕业于广西医科大学临床专业硕士', '武警广西总队医院', 4.9, 0.97, 357, 4.2);
INSERT INTO `doctor` VALUES (10015, 1, '梁学超', 'abcd1234', '09.jpg', '高血压', 2, '擅长心血管内科，心血管内科及其他普通内科的常见疾病及多发病的诊断及治疗，急危重症医学病症的诊疗。', '2009年毕业于赣南医学院。', '广西科技大学第一附属医院', 4.6, 0.94, 134, 3);
INSERT INTO `doctor` VALUES (10016, 1, '毛亮', 'abcd1234', '10.jpg', '高血压', 2, '本人擅长复杂高血压、冠心病及心力衰竭的治疗是我的强项，常治老年堵塞性血压病。', '2014年毕业于贵阳医学院，现更名为贵州医科大学。', '贵阳市第四人民医院', 4.6, 0.93, 785, 4.6);
INSERT INTO `doctor` VALUES (10017, 1, '熊力', 'abcd1234', '11.jpg', '高血压', 3, '心血管疾病，如高血压，冠心病，各种心律失常，心肌病的临床诊治，特别是中老年病人的心血管疾病。', '2011年南昌大学心内科硕士', '江西省人民医院', 4.8, 0.94, 624, 4.5);
INSERT INTO `doctor` VALUES (10018, 1, '叶城辉', 'abcd1234', '12.jpg', '高血脂', 4, '神经系统常见疾病 （脑血管病，痴呆，帕金森病）及神经肌肉遗传病（肌营养不良），肌萎缩。', '2012年毕业于中山大学，取得临床医学硕士学位', '中山大学附属第一医院', 4.9, 0.99, 462, 4.2);
INSERT INTO `doctor` VALUES (10019, 2, '范宇琴', 'abcd1234', '41.jpg', '冠心病', 4, '冠心病，高血压，心律失常，心绞痛，心肌病等心血管常见病多发病的诊治及相关健康咨询。', '硕士研究生', '南昌大学第一附属医院', 4.9, 0.97, 1221, 5);
INSERT INTO `doctor` VALUES (10020, 1, '陈志龙', 'abcd1234', '13.jpg', '冠心病', 2, '高血压病，冠心病，心力衰竭，心律失常等！慢性病长期随访及诊疗！有时去治愈，常常去帮助，总是去安慰！', '福建医科大学临床医学系', '莆田市第一医院', 4, 0.85, 830, 4.6);
INSERT INTO `doctor` VALUES (10021, 1, '吴海云', 'abcd1234', '14.jpg', '冠心病', 2, '高血压病、冠心病诊治及介入治疗、心律失常诊治及射频消融术、起搏器植入术及程控', '2000年毕业于福建医科大学，毕业后就职于福建医科大学附属泉州第一医院心内科（三级甲等医院），2009年09月-2010年08月于北京大学人民医院心内科进修。', '福建省泉州第一医院', 4.2, 0.89, 553, 4.1);
INSERT INTO `doctor` VALUES (10022, 1, '李富浩', 'abcd1234', '15.jpg', '冠心病', 2, '心脑血管，呼吸，消化系统,泌尿系统疾病诊治。擅长冠心病、脑梗死、慢性支气管炎、支气管哮喘、胃炎等疾病的中西医结合治疗', '临床医学专业', '广州市荔湾区第二人民医院', 4.3, 0.91, 151, 3.3);
INSERT INTO `doctor` VALUES (10023, 2, '蒋红双', 'abcd1234', '42.jpg', '冠心病', 3, '提供高血压、冠心病、糖尿病、脑梗等心脑血管疾病的专业的用药指导，以及健康教育的宣教。', '2013年毕业于广西医科大学临床专业硕士', '武警广西总队医院', 4.7, 0.95, 774, 4.5);
INSERT INTO `doctor` VALUES (10024, 1, '刘善湖', 'abcd1234', '16.jpg', '冠心病', 1, '冠心病、高血压、心力衰竭、心律失常等常见病及疑难杂症的诊治，擅长高龄者心血管介入的诊治', '2017年毕业于南昌大学，硕士研究生学历', '上饶市人民医院', 3.7, 0.87, 567, 4.2);
INSERT INTO `doctor` VALUES (10025, 1, '尹海滨', 'abcd1234', '17.jpg', '冠心病', 1, '本人精通心血管疾病的诊断及治疗，先后在南昌大学第二附属医院和北京佑安医院进修学习，并且参加过国家心血管疾病进修培训班，希望能为大家服务。', '大学本科毕业，从医11年，2012年取得主治医师证，先后在南昌、北京医院进修学习。', '吉安市中心人民医院', 3.8, 0.85, 644, 4.5);
INSERT INTO `doctor` VALUES (10026, 1, '梅乾彰', 'abcd1234', '18.jpg', '冠心病', 2, '内科、皮肤科等常见疾病及多发病的中西医结合诊治。内科如中风、失眠、冠心病、高血压、糖尿病、慢性支气管炎、慢性胃炎、肝炎肝硬化、便秘等及其他杂病等。', '主治医师，研究生学历，硕士学位，2013年毕业于北京中医药大学中医内科专业。', '贵州医科大学第二附属医院', 4.6, 0.93, 324, 3.9);
INSERT INTO `doctor` VALUES (10027, 1, '陈武冲', 'abcd1234', '19.jpg', '骨质疏松', 2, '同一疾病，提供中医、西医的治疗建议。我系中西医结合主治医师，长期在基层从事临床工作。擅长骨病及骨折的中西医结合治疗。', '1.2003年本科毕业于江西省中医学院。 2.2010年参加卫生部“走进西部”基层骨干医师培训。 3.2010年参加“江西中医适宜技术推广培训班”。', '崇义县中医院', 4.1, 0.9, 136, 3.1);
INSERT INTO `doctor` VALUES (10028, 1, '林桂锋', 'abcd1234', '20.jpg', '骨质疏松', 2, '骨质疏松、糖尿病、冠心病、高血压、慢阻肺、甲状腺疾病等相关方面的常见病、多发病的诊断与治疗。', '牡丹江医学院本科学历', '莆田市第一医院', 4.5, 0.95, 974, 4.6);
INSERT INTO `doctor` VALUES (10029, 1, '荣冬明', 'abcd1234', '21.jpg', '骨质疏松', 3, '擅长髋膝关节疾病，四肢骨折、关节脱位，手外伤，腰椎间盘突出等骨科常见疾病的诊断与治疗，尤其是老年缺钙引发的骨质疏松。', '南方医科大学骨科硕士研究生毕业', '南方医科大学珠江医院', 4.7, 0.96, 567, 4.5);
INSERT INTO `doctor` VALUES (10030, 1, '钟佳权', 'abcd1234', '22.jpg', '骨质疏松', 4, '擅长骨质增生，腰椎间盘突出，腰腿疼，颈部疼痛，四肢乏力，以及各类型骨折，手外科疾病，肩周炎，小儿骨科等。', '在暨南大学攻读本硕八年。', '香港大学深圳医院', 4.9, 0.95, 451, 4.2);
INSERT INTO `doctor` VALUES (10031, 1, '蔡继伟', 'abcd1234', '23.jpg', '骨质疏松', 2, '1.腰腿疼痛，颈椎病，运动系统疾病。 2.掌握骨科常见疾病的有关理论知识、断与鉴别诊断、外科治疗原则及手术适应症。 3.掌握现代骨科检查方法。 ', '本人毕业于安徽医科大学临床医学院，从事创伤骨外科临床工作5年', '湛江中心人民医院', 4.6, 0.94, 123, 2.5);
INSERT INTO `doctor` VALUES (10032, 1, '盛关云', 'abcd1234', '30.jpg', '骨质疏松', 1, '1.骨关节炎、骨质疏松、骨感染、骨肿瘤等骨关节疾病的中西医诊治；2.肩肘腕膝踝关节的运动损伤疾病的中西医诊治，如肩袖损伤、膝交叉韧带锻炼、踝关节扭伤等；', '硕士毕业于广州中医药大学、广东省中医院骨科', '柳州市中医院', 3.9, 0.86, 787, 4.6);
INSERT INTO `doctor` VALUES (10033, 1, '蒋文勇', 'abcd1234', '25.jpg', '骨质疏松', 2, '擅长腰腿痛，颈肩痛，腰椎间盘突出症，颈椎病，腰椎管狭窄症，腰肌劳损及髋膝关节炎，半月板损伤，膝关节韧带损伤，股骨颈骨折，四肢骨折，痛风，骨肿瘤等常见病的诊疗。', '毕业于中国医科大学，医学硕士。曾于国内核心期刊发表论文5篇。随导师参与一项国家级重点课题的实验及论文纂写。', '桂林市人民医院', 4.3, 0.93, 367, 4);
INSERT INTO `doctor` VALUES (10034, 1, '李白林', 'abcd1234', '31.jpg', '骨质疏松', 1, '骨科，尤擅长骨关节病、骨髓炎、骨折不愈合、骨延长、腰腿痛的治疗、腰椎间盘突出症、梨状肌综合症、第三腰椎横突综合症、腰肌劳损、急性腰扭伤等治疗', '云南中医学院中西医结合临床专业毕业', '西双版纳州傣医医院', 3.5, 0.87, 647, 4.5);
INSERT INTO `doctor` VALUES (10035, 2, '刘莎', 'abcd1234', '44.jpg', '骨质疏松', 2, '糖尿病、骨质疏松、甲状腺疾病、痛风、肥胖症等内分泌科疾病以及各种疾病的营养治疗、饮食指导等等。', '本科专业为临床医学，硕士研究生为骨外科，主攻骨质疏松及颈椎病疾病。', '南昌大学第二附属医院', 4.6, 0.95, 268, 2.9);
INSERT INTO `doctor` VALUES (10036, 1, '熊平', 'abcd1234', '27.jpg', '哮喘病', 1, '擅长感冒，急性支气管炎，肺炎，慢性支气管炎，咯血，哮喘，腹痛，腹泻，冠心病，高血压，糖尿病，肝炎，尿路感染，结石等内科常见病及多发病的诊疗。', '从学校本科毕业后一直工作在临床一线。', '庐山市人民医院', 3.6, 0.84, 246, 3.1);
INSERT INTO `doctor` VALUES (10037, 1, '江训盛', 'abcd1234', '29.jpg', '哮喘病', 2, '各种原因到致的胸痛胸闷、肺部肿瘤、肺部感染咳嗽、咳痰、咯血等呼吸系统疾病的诊断治疗预防', '呼吸内科医学硕士毕业，擅长呼吸科疾病的诊治，尤其是哮喘，上呼吸道感染，慢性阻塞性肺病等', '南昌市第三医院', 4.2, 0.9, 886, 4.6);
INSERT INTO `doctor` VALUES (10038, 1, '黄惟彬', 'abcd1234', '33.jpg', '哮喘病', 2, '擅长内科常见疾病，如感冒，肺炎，慢性阻塞性肺疾病，支气管哮喘，支气管扩张，胸痛，腹泻，咽痛，咽痒，咳嗽等常见病的诊断与治疗以及体检报告解读。', '医学研究所毕业', '中山大学附属第三医院', 4.3, 0.89, 145, 3.2);
INSERT INTO `doctor` VALUES (10039, 1, '林良兜', 'abcd1234', '36.jpg', '哮喘病', 3, '从事内科、儿科、中医呼吸科临床工作10余年。主要擅长：儿科、消化内科、中老年呼吸科疾病的中西医结合诊疗。', '2002.9-2007.6 中医学本科 2015.9-2018.6 呼吸内科硕士研究生', '解放军第四二一医院', 4.8, 0.96, 984, 4.6);
INSERT INTO `doctor` VALUES (10040, 1, '黄为彬', 'abcd1234', '37.jpg', '哮喘病', 2, '擅长内科常见疾病，如感冒，肺炎，慢性阻塞性肺疾病，支气管哮喘，支气管扩张，胸痛，腹泻，咽痛，咽痒，咳嗽等常见病的诊断与治疗以及体检报告解读。', '医学研究生毕业', '中山大学附属第三医院', 4.4, 0.92, 145, 2.6);
INSERT INTO `doctor` VALUES (10041, 2, '陈健霞', 'abcd1234', '45.jpg', '哮喘病', 4, '毕业后三甲医院工作两年，擅长呼吸科疾病的诊治！ 对于内科常见病，例呼吸、心内、肾内、神经内科等疾病的诊治', '广州医科大学硕士研究生毕业，从事重症医学数年', '广州医科大学附属第二医院', 4.9, 0.96, 645, 4.5);
INSERT INTO `doctor` VALUES (10042, 1, '郑候喜', 'abcd1234', '39.jpg', '哮喘病', 2, '2001年毕业于右江民族医学院临床医学本科，学士学位，于2005年广西医科大学研究生毕业，曾多次到中山医学院呼吸科及广州呼研所进修学习。', '研究生学历，曾多次到中山医学院呼吸科及广州呼研所进修学习。', '广西钦州市妇幼保健院', 4.6, 0.96, 537, 4.5);
INSERT INTO `doctor` VALUES (10043, 2, '杨见辉', 'abcd1234', '46.jpg', '哮喘病', 2, '贵州省中医药学会中医呼吸科专业委员会委员，贵州省中西医结合学会呼吸科专业委员会委员。毕业于贵阳中医学院中西医结合临床专业，从事呼吸科临床、教学工作7年，擅长中西医结合治疗呼吸科常见疾病', '主持完成省教育厅课题1项、院级课题1项，参与国家中医药管理局课题2项。', '贵阳中医学院第二附属医院', 4.3, 0.92, 157, 2.7);
INSERT INTO `doctor` VALUES (10044, 1, '申兵', 'abcd1234', '40.jpg', '哮喘病', 1, '呼吸疾病及心血管内科疾病，华南农业大学医院一所社区卫生服务中心，主要以全科门诊，内，外，妇，耳鼻喉，口腔科门诊，为社区居民提供全方位的服务。', '广州医科大学临床医学专业，在职攻读了广州医科大学内科硕士', '广州医学院第二附属医院', 3.7, 0.89, 963, 4.6);
INSERT INTO `doctor` VALUES (10045, 1, '潘来生', 'abcd1234', '43.jpg', '慢性支气管炎', 1, '内科常见病，呼吸消化等多发病的诊治，对小儿呼吸，消化，辅食添加都比较擅长，希望帮助你', '2002-2005宜春学院临床医学大专毕业 2011-2014宜春学院临床医学本科毕业 2005-2015宜春市第三人民医院工作', '宜春市第三人民医院', 3.8, 0.88, 427, 3.6);
INSERT INTO `doctor` VALUES (10046, 1, '钟善坚', 'abcd1234', '48.jpg', '慢性支气管炎', 2, '结核病及呼吸内科常见病如慢支肺心病、支扩、肺结核等诊断 及治疗，对肝炎及大三小阳、过敏性鼻炎也有丰富经验。', '桂林医学院89年毕业', '广州市番禺区慢性病防治站', 4.5, 0.93, 137, 2.9);
INSERT INTO `doctor` VALUES (10047, 2, '肖曼', 'abcd1234', '47.jpg', '慢性支气管炎', 4, '擅长呼吸内科疾病如感冒发烧、咳嗽咽痛、咽喉炎、肺气肿、支气管哮喘的中西医治疗，常见内科疾病的中西医诊疗，指导中医养生，中医体质分析，中医调理体质等等', '广州中医药大学研究生毕业', '广州市中医院', 4.9, 0.97, 1145, 4.8);
INSERT INTO `doctor` VALUES (10048, 1, '许呢妹', 'abcd1234', '49.jpg', '慢性支气管炎', 2, '熟悉掌握内、外、妇、儿各科常见病、多发病诊疗常规，专业知识全面、扎实。2011年开始从事重症医学工作，现为江门市中心医院重症医学科主治医师，临床研究方向为呼吸系统疾病。', '2002-2007年中山大学临床医学专业； 2008-2011年江门市中心医院规范化培训；2012-2014年广州呼吸研究所在职研究生。', '江门市中心医院', 4.6, 0.97, 1342, 4.7);
INSERT INTO `doctor` VALUES (10049, 1, '申兵', 'abcd1234', '50.jpg', '慢性支气管炎', 2, '呼吸疾病及心血管内科疾病，华南农业大学医院一所社区卫生服务中心，主要以全科门诊，内，外，妇，耳鼻喉，口腔科门诊，为社区居民提供全方位的服务。', '广州医科大学临床医学专业，在职攻读了广州医科大学呼吸内科硕士', '广州医学院第二附属医院', 4.3, 0.91, 963, 4.6);
INSERT INTO `doctor` VALUES (10050, 2, '梁忆华', 'abcd1234', '26.jpg', '慢性支气管炎', 1, '支气管哮喘、支气管扩张、慢性阻塞性肺疾病、肺炎、支气管炎、上呼吸道感染、慢性咳嗽等常见呼吸道疾病', '毕业学校：广州医学院', '广州红十字会医院', 3.8, 0.89, 512, 4.5);
INSERT INTO `doctor` VALUES (10051, 1, '张德华', 'abcd1234', '51.jpg', '慢性支气管炎', 2, '擅长常见呼吸系统疾病，如慢性咳嗽，支气管哮喘，慢性阻塞性肺疾病，肺部肿瘤等疾病的诊治', '广州呼吸疾病研究所硕士研究生', '惠州市第三人医院', 4.4, 0.91, 515, 4.5);
INSERT INTO `doctor` VALUES (10052, 2, '陈健霞', 'abcd1234', '93.jpg', '慢性支气管炎', 3, '毕业后三甲医院工作两年，擅长内科疾病的诊治！ 对于内科常见病，例呼吸、心内、肾内、神经内科等疾病的诊治', '广州医科大学硕士研究生毕业，从事重症医学数年', '广州医科大学附属第二医院', 4.7, 0.93, 347, 4);
INSERT INTO `doctor` VALUES (10053, 1, '罗锋', 'abcd1234', '52.jpg', '慢性支气管炎', 2, '长期从事临床工作，对呼吸系统疾病的诊治有丰富的临床经验，主要临床方向为慢性阻塞性肺疾病和肺心病、肺部感染、气道炎症、哮喘等，对气道炎症、哮喘、慢性咽炎的诊治具有独到的处理方法。', '男，医学博士。1997年毕业华西医科大学，获医学学士学位，2003年获医学硕士学位，2010年获医学博士学位。曾在美国知名医院进修学习。', '四川大学华西医院', 4.5, 0.91, 1359, 4.7);
INSERT INTO `doctor` VALUES (10054, 1, '陈武冲', 'abcd1234', '53.jpg', '痛风性关节炎', 3, '同一疾病，提供中医、西医的治疗建议。我系中西医结合主治医师，长期在基层从事临床工作。擅长骨病及骨折的中西医结合治疗。', '1.2003年本科毕业于江西省中医学院。 2.2010年参加卫生部“走进西部”基层骨干医师培训。 3.2010年参加“江西中医适宜技术推广培训班”。', '崇义县中医院', 4.8, 0.95, 168, 2.8);
INSERT INTO `doctor` VALUES (10055, 1, '陈武冲', 'abcd1234', '54.jpg', '肩周炎', 1, '同一疾病，提供中医、西医的治疗建议。我系中西医结合主治医师，长期在基层从事临床工作。擅长骨病及骨折的中西医结合治疗。', '1.2003年本科毕业于江西省中医学院。 2.2010年参加卫生部“走进西部”基层骨干医师培训。 3.2010年参加“江西中医适宜技术推广培训班”。', '崇义县中医院', 3.3, 0.86, 168, 2.9);
INSERT INTO `doctor` VALUES (10056, 1, '闵志云', 'abcd1234', '55.jpg', '肩周炎', 3, '腰椎间盘突出,面肌痉挛,带状疱疹,肩周炎,颈椎病,中风病,失眠,面神经麻痹,痛经,月经失调,小儿感冒,肥胖症,头痛,眩晕,抑郁症,功能性消化不良,高血压,糖尿病,高脂血症,小儿湿疹,小儿支气管炎,小儿肠炎', '在顺德区桂洲医院从事中医科工作过，主修神经、运动系统疾病的江西省中医院神经内科及针灸康复科工作过。', '江西康宁医院', 4.8, 0.95, 317, 3.8);
INSERT INTO `doctor` VALUES (10057, 1, '荣冬明', 'abcd1234', '56.jpg', '肩周炎', 2, '擅长髋膝关节疾病，四肢骨折、关节脱位，手外伤，腰椎间盘突出等骨科常见疾病的诊断与治疗。', '南方医科大学骨科硕士研究生毕业。', '南方医科大学珠江医院', 4.1, 0.92, 457, 4.5);
INSERT INTO `doctor` VALUES (10058, 1, '钟佳权', 'abcd1234', '57.jpg', '肩周炎', 4, '擅长骨质增生，腰椎间盘突出，腰腿疼，颈部疼痛，四肢乏力，以及各类型骨折，手外科疾病，肩周炎，小儿骨科等', '在暨南大学攻读本硕八年。', '香港大学深圳医院', 4.9, 0.96, 137, 2.6);
INSERT INTO `doctor` VALUES (10059, 2, '雷艳', 'abcd1234', '94.jpg', '肩周炎', 2, '擅长辩证治疗中医内科的常见病、多发病及针灸治疗如面瘫、偏瘫、失眠、颈椎病、肩周炎、腰肌劳损等症。', '中医骨外科主治医师', '南宁市第八人民医院', 4.6, 0.93, 986, 4.6);
INSERT INTO `doctor` VALUES (10060, 1, '蒋文勇', 'abcd1234', '58.jpg', '肩周炎', 1, '擅长腰腿痛，颈肩痛，腰椎间盘突出症，颈椎病，腰椎管狭窄症，腰肌劳损及髋膝关节炎，半月板损伤，膝关节韧带损伤，股骨颈骨折，四肢骨折，痛风，骨肿瘤等常见病的诊疗。', '毕业于中国医科大学，医学硕士。曾于国内核心期刊发表论文5篇。随导师参与一项国家级重点课题的实验及论文纂写。', '桂林市人民医院', 3.8, 0.89, 367, 3.8);
INSERT INTO `doctor` VALUES (10061, 1, '吴迪', 'abcd1234', '59.jpg', '肩周炎', 2, '擅长骨外普外手术、关节病、神经、肌腱损伤，慢性骨髓炎、骨关节损伤、骨结核、颈肩腰腿痛、风湿、类风湿、痛风、骨折、脱位的复位内外固定手术。', '毕业于贵阳中医药大学中西医临床医学，大学本科，工作于毕节市第二人民医院，曾经在广州军区总医院骨科进修学习关节镜治疗技术。', '毕节市第二人民医院', 4.5, 0.94, 137, 2.8);
INSERT INTO `doctor` VALUES (10062, 1, '谢柏臻', 'abcd1234', '60.jpg', '肩周炎', 2, '长期从事脊柱疾病的诊治。擅长以各种脊柱微创手术治疗椎间盘突出，椎管狭窄，滑脱等疾病。', '华中科技大学同济医学院附属同济医院 骨科博士', '厦门大学附属中山医院', 4.4, 0.95, 438, 4.1);
INSERT INTO `doctor` VALUES (10063, 1, '杨振东', 'abcd1234', '61.jpg', '老年痴呆', 2, '中风（脑卒中）、焦虑、抑郁、老年痴呆、帕金森病、肌张力障碍、癫痫、脑炎、多发硬化、视神经脊髓炎、吉兰巴雷综合征、线粒体脑肌病、不宁腿综合征、雷诺综合征等。', '医学博士，主治医师，2013年毕业于华中科技大学同济医学院骨科专业，2013.7-2015.6在江苏徐州中心医院工作并完成住院医师规范化培训。', '武汉市第一医院', 4.6, 0.93, 347, 3.7);
INSERT INTO `doctor` VALUES (10064, 1, '付生麒', 'abcd1234', '62.jpg', '老年痴呆', 2, '神经内科常见疾病诊断与治疗，主要方向为脑血栓，脑梗塞，脑出血，缺血性卒中介入治疗，溶栓治疗，重症肌无力，自身免疫性脑炎，神经免疫性疾病，失眠，多梦，老年痴呆等。', '硕士研究生', '自贡市第一人民医院', 4.1, 0.9, 567, 4.5);
INSERT INTO `doctor` VALUES (10065, 1, '张霖', 'abcd1234', '63.jpg', '老年痴呆', 4, '医学博士，擅长头痛，麻木，抽搐，抽筋，多梦，脑梗塞，脑供血不足，手抖，行走不稳，帕金森，脑炎，焦虑，抑郁，神经痛，面瘫，肌炎，无力，瘫痪，痴呆，记忆力差等', '张霖，男，四川大学华西医院，医学博士', '四川大学华西医院', 4.9, 0.98, 157, 3.1);
INSERT INTO `doctor` VALUES (10066, 1, '万鸿平', 'abcd1234', '64.jpg', '老年痴呆', 2, '脑血管病，痴呆与认知障碍，帕金森，癫痫，颅内感染及各种头痛，睡眠障碍等神经内科疾病及心血管、消化、呼吸科常见病的诊治。', '1997年同济大学医学院本科毕业，2006年武汉大学医学院研究生毕业，工作17 年', '湖北第三人民医院', 4.3, 0.95, 984, 4.6);
INSERT INTO `doctor` VALUES (10067, 1, '姜新昌', 'abcd1234', '65.jpg', '老年痴呆', 1, '急诊科 内科呼吸道疾病心脑血管病消化系统疾病泌尿系等常见病，多发病，及急危重症疾病', '自幼对医药书籍感兴趣，学医从医有二十八年余，从基层做起，从全科医生做起，工作学习认真刻苦，取得了一些成绩。', '鄂州市中医院', 3.8, 0.82, 567, 4.5);
INSERT INTO `doctor` VALUES (10068, 1, '谭贤佩', 'abcd1234', '66.jpg', '老年痴呆', 2, '擅长头晕，头痛，脑血管病诊断治疗及预防康复等问题，对神经心理疾病诊治等有独到见解。同时熟悉帕金森、癫痫、周围神经疾病等其他常见神经系统疾病的诊治。熟悉高血压，糖尿病等一般内科疾病的诊疗。', '湘雅医院研究生毕业，神经内科主治医师，2013年至今在荆州第一人民医院工作。', '荆州市第一人民医院 ', 4.6, 0.96, 451, 4);
INSERT INTO `doctor` VALUES (10069, 1, '谢逸群', 'abcd1234', '67.jpg', '老年痴呆', 3, '头昏，头痛，失眠，眩晕，晕倒，肌跳无力麻木抖动，脑梗塞脑缺血，脑出血，脱髓鞘，脑炎，癫痫，运动神经元病（渐冻症），运动异常，焦虑抑郁以及其他神经系统问题。', '湘雅求学，神经医学专业临床医生，临床神经医学博士。', '中南大学湘雅二医院', 4.7, 0.96, 347, 3.6);
INSERT INTO `doctor` VALUES (10070, 1, '黄剑涛', 'abcd1234', '68.jpg', '老年痴呆', 2, '毕业于湖南中医药大学，硕士研究生学历，现为脑病科大夫，擅长中风，眩晕，头痛等病的中西医结合诊治。掌握一整套耳石症复位手法。', '毕业于湖南中医药大学，硕士研究生学历，现为脑病科大夫，擅长中风，眩晕，头痛等病的中西医结合诊治。', '常德市中医院', 4.6, 0.94, 237, 2.6);
INSERT INTO `doctor` VALUES (10071, 1, '黄耀忠', 'abcd1234', '69.jpg', '老年痴呆', 1, '擅长头痛、头晕、癫痫、睡眠障碍、痴呆、脑血管疾病、中枢神经系统感染、周围神经疾病、急性脊髓炎、多发性硬化、帕金森病、神经症、周期性瘫痪的诊治。', '毕业于中山医科大学临床医学系临床医学专业，现任广东省老年保健协会神经内科专业委员会常务委员，广东省康复医学会康复治疗专业委员会委员员。', '中信惠州医院', 3.5, 0.87, 964, 4.6);
INSERT INTO `doctor` VALUES (10072, 1, '范长秋', 'abcd1234', '70.jpg', '阻塞性肺气肿', 2, '感冒，咳嗽，变异性咳嗽，鼻窦炎，过敏性鼻炎，哮喘，肺癌中药治疗，胸膜炎，急慢性咽炎，扁桃体炎，阳痿早泄，闭经，多囊卵巢综合征，慢性胃炎，失眠，更年期综合征等疾病。', '范长秋，主任医师。毕业于湖北中医药大学。从事内科工作27年，现任湖北省利川市民族中医院呼吸科主任，省级重点专科学科带头人。', '利川市民族中医院', 4.1, 0.92, 457, 4.2);
INSERT INTO `doctor` VALUES (10073, 1, '郭宗喜', 'abcd1234', '71.jpg', '阻塞性肺气肿', 2, '各种呼吸科常见病及多发病！长阳中医院是一家二甲中西医结合医院，我们本着患者至上的原则，用心为每位患者服务！', '毕业于湖北中医药大学，多次赴上级医院进修学习！', '长阳县中医院', 4.3, 0.93, 157, 3.2);
INSERT INTO `doctor` VALUES (10074, 1, '李成', 'abcd1234', '72.jpg', '阻塞性肺气肿', 2, '发热、感冒、支气管哮喘、慢性咳嗽、肺炎、肺结核、过敏性鼻炎、支气管扩张、肺结核、慢性阻塞性肺病的诊断、肺气肿、肺心病、肺炎等感染性疾病诊断治疗有多年经验。', '毕业于中南大学湘雅医学院的呼吸内科硕士研究生，在湘雅医院学习三年，毕业后在市中心医院工作2年', '邵阳市中心医院', 4.5, 0.94, 134, 2.9);
INSERT INTO `doctor` VALUES (10075, 1, '郑蜀军', 'abcd1234', '73.jpg', '阻塞性肺气肿', 1, '擅长感冒、支气管哮喘、肺炎、过敏性鼻炎、支气管扩张、肺结核、慢性阻塞性肺病等呼吸系统常见病、多发病的诊断治疗。', '主治医师 本科毕业 从业16年 先后在湘雅附二 南华附一进修 擅长诊断治疗内科常见病 多发病', '耒阳市疾病预防控制中心', 3.7, 0.82, 674, 4.5);
INSERT INTO `doctor` VALUES (10076, 1, '唐勇', 'abcd1234', '74.jpg', '阻塞性肺气肿', 2, '擅长呼吸道感染，肺炎，慢阻肺，肺心病，肺间质疾病，胸腔积液，支气管扩张，哮喘，肺结核，肺部结节，肺癌等呼吸科常见病及疑难病诊治。', '2001年川北医学院五年制临床医学本科毕业，2009年在四川大学华西医院呼吸内科，肺癌中心及纤支镜室进修，2011年在四川大学读研究生，2014年获四川大学华西临床医学院呼吸内科硕士学位。', '武警四川省总队成都医院', 4.6, 0.96, 123, 2.8);
INSERT INTO `doctor` VALUES (10077, 2, '柳涛红', 'abcd1234', '95.jpg', '阻塞性肺气肿', 1, '支气管哮喘，慢性阻塞性肺疾病，肺炎，肺结核，肺脓肿，肺癌，气胸，上呼吸道感染，支气管炎', '川北医学院本科，川北医学院临床型研究生', '川北医学院附属医院', 3.4, 0.84, 156, 3.1);
INSERT INTO `doctor` VALUES (10078, 2, '张冬梅', 'abcd1234', '96.jpg', '阻塞性肺气肿', 4, '擅长于慢性支气管炎、哮喘、慢性阻塞性肺部疾病、肺心病、肺炎、肺部肿瘤等常见肺部疾病诊治，对心脑血管疾病、慢性胃炎、消化道溃疡、肿瘤等疾病有一定研究。', '毕业于川南医科大学（原泸州医学院）临床医学，从事临床工作4年余，熟悉临床常见病、多发病如阻塞肺气肿、慢性支气管炎、肺气肿、等的诊疗。', '四川省人民医院', 4.9, 0.99, 789, 4.5);
INSERT INTO `doctor` VALUES (10079, 1, '钟文', 'abcd1234', '77.jpg', '阻塞性肺气肿', 3, '呼吸系统疾病及糖尿病，肺癌的诊断及治疗，肿瘤的靶向治疗，糖尿病常见并发症的诊断及治疗', '2013年湘雅医学院临床医学本科毕业 2016年湘雅医院呼吸内科硕士毕业 2016年至今湘雅医院老年内分泌科博士在读', '中南大学湘雅医院', 4.8, 0.97, 642, 4.5);
INSERT INTO `doctor` VALUES (10080, 1, '叶城辉', 'abcd1234', '78.jpg', '脑卒中', 2, '神经系统常见疾病 （脑血管病，痴呆，帕金森病）及神经肌肉遗传病（肌营养不良），肌萎缩', '2012年毕业于中山大学，取得神经病学硕士学位', '中山大学附属第一医院', 4.3, 0.9, 457, 4.3);
INSERT INTO `doctor` VALUES (10081, 1, '张文武', 'abcd1234', '79.jpg', '脑卒中', 2, '擅长：一.抑郁症、焦虑症；二.睡眠障碍；三.各类偏头痛及神经官能症；四.周围性面瘫、脑卒中后遗症、各种外伤后运动功能障碍。', '张文武主治医：2006年6月毕业于安徽医科大学第一临床学院； 2006年7月就业于合肥市第三人民医院； 2014年5月毕业于安徽医科大学研究生学院。', '合肥市第三人民医院', 4.3, 0.89, 647, 4.5);
INSERT INTO `doctor` VALUES (10082, 1, '刘创谋', 'abcd1234', '80.jpg', '脑卒中', 2, '本科毕业，神内工作至今五年了，对于神经，肌肉运动，脑血管疾病的诊疗比较熟悉，微末之力希望能帮的上忙', '本科临床医学毕业', '梅州市中医院', 4.5, 0.93, 127, 4);
INSERT INTO `doctor` VALUES (10083, 1, '黄耀忠', 'abcd1234', '81.jpg', '脑卒中', 2, '擅长头痛、头晕、癫痫、睡眠障碍、痴呆、脑血管疾病、中枢神经系统感染、周围神经疾病、急性脊髓炎、多发性硬化、帕金森病、神经症、周期性瘫痪的诊治。', '毕业于中山医科大学临床医学系临床医学专业，现任广东省老年保健协会神经内科专业委员会常务委员，广东省康复医学会康复治疗专业委员会委员员。', '中信惠州医院', 4.1, 0.92, 964, 4.6);
INSERT INTO `doctor` VALUES (10084, 1, '李隆文', 'abcd1234', '82.jpg', '脑卒中', 4, '内科常见疾病的诊治，另外兼有对祖国医学的认识，可以指导一定程度的养生保健的方法及人生哲学的认识。擅长神经科疾病的诊治，主攻方向是脑血管疾病的诊治及精神心理疾病的诊治', '中山大学医学院神经内科学硕士研究生，临床经历20余年，临床工作业务及解决问题的能力较强', '广州市萝岗中医医院', 4.9, 0.96, 657, 4.5);
INSERT INTO `doctor` VALUES (10085, 1, '潘必树', 'abcd1234', '83.jpg', '脑卒中', 2, '擅长神经内科.急救医学，心血管.呼吸内科.消化内科方面的诊疗，对于儿科常见病也有一定经验。', '大学本科毕业于湖北医药学院！', '兴宁市人民医院', 4.2, 0.92, 467, 4.2);
INSERT INTO `doctor` VALUES (10086, 1, '于斯文', 'abcd1234', '84.jpg', '脑卒中', 2, '脑血管疾病 眩晕症 面神经炎 面肌痉挛 坐骨神经痛 头痛 癫痫 帕金森病 痴呆等疾病的预防与治疗', '于2009年毕业于辽宁医学院', '解放军第四二一医院', 4.2, 0.93, 345, 3.9);
INSERT INTO `doctor` VALUES (10087, 1, '廖秋', 'abcd1234', '85.jpg', '脑卒中', 2, '本诊所负责人为神经内科主任医师，教授。从事神经内科一线工作近20年。擅长：脑血管疾病诊治；神经血管介入治疗；头晕、头痛诊治；癫痫的诊治；脱髓鞘疾病诊治', '中山大学医学院医学硕士', '茂名市人民医院', 4.3, 0.94, 342, 3.8);
INSERT INTO `doctor` VALUES (10088, 2, '任庆华', 'abcd1234', '97.jpg', '脑卒中', 1, '熟练掌握脑梗塞、脑出血、蛛网膜下腔出血、帕金森、癫痫、脑炎、头晕头痛等神经系统常见病、多发病的救治', '神经病学硕士', '江门市人民医院', 3.3, 0.87, 674, 4.5);
INSERT INTO `doctor` VALUES (10089, 1, '张祖建', 'abcd1234', '87.jpg', '脑卒中', 3, '焦虑，抑郁症，强迫症精神分裂症等相关问题的咨询。对您的健康，我用青春的经历及学业的努力，谱写心灵的健康之路。', '本科就读于井冈山大学，在南昌大学参加第十期心理咨询师培训。', '江西省人民医院', 4.8, 0.95, 861, 4.6);
INSERT INTO `doctor` VALUES (10090, 1, '李忠东', 'abcd1234', '88.jpg', '肝硬化', 1, '肝炎、肝硬化、肝结核。', '从事肝硬化等肝区疾病治疗20余年，在肝硬化方面有独特的治疗方法和先进的治疗经验，能够很好的把控肝病的病情。', '兴国县人民医院', 3.8, 0.9, 345, 4);
INSERT INTO `doctor` VALUES (10091, 1, '王春喜', 'abcd1234', '89.jpg', '肝硬化', 2, '慢性乙肝、丙肝等病毒性肝炎的治疗，特别擅长乙肝、丙肝病毒扩散的治疗，中药赤黄汤治疗各型肝炎。', '毕业于第三军医大学医疗系，在解放军传染病中心302医院专修一年 ，从事肝病治疗20余年。', '上饶市第二人民医院', 4.6, 0.91, 118, 3.4);
INSERT INTO `doctor` VALUES (10092, 2, '李伟', 'abcd1234', '98.jpg', '肝硬化', 4, '乙型肝炎、丙型肝炎、药物性肝损害，自身免疫性肝病，特别擅长肝硬化治疗。', '从事肝病治疗临床、教学、科研工作20余年，熟练掌握各种肝病的诊治，特别是慢性乙、丙型肝炎和肝硬化的抗病毒治疗。', '武汉协和医院', 4.9, 0.96, 1329, 4.8);
INSERT INTO `doctor` VALUES (10093, 1, '方清', 'abcd1234', '91.jpg', '肝硬化', 2, '各种肝脏疾病咨询，致力于慢性肝病的健康教育和定期随访管理的推广及研究。', '毕业于华西医科大学，获临床学士学位，2008年开始从事肝硬化并发症及其发生机理的研究和临床工作，对于肝硬化的诱发机理有着深入的研究。', '宜昌市中心医院', 4.5, 0.94, 654, 4.5);
INSERT INTO `doctor` VALUES (10094, 1, '余海滨', 'abcd1234', '75.jpg', '肝硬化', 2, '乙肝、丙肝，肝炎，脂肪胺，肝硬化，肝癌，脾肿大，脾功能亢进，食管静脉曲张，肝功能异常的治疗。', '主治医师，从事肝病临床治疗10余年，曾在复旦大学附属肿瘤医院进修半年，现为江西省抗癌协会肝癌专业青年委员会委员，南昌市肿瘤协会委员，南昌市肝病协会委员。', '南昌市第九医院', 4.4, 0.92, 298, 3.6);
INSERT INTO `doctor` VALUES (10095, 1, '徐海丰', 'abcd1234', '76.jpg', '脑梗塞', 1, '从事临床工作3年，在南昌大学第一附属医院进修心血管内科，重症医学科1年，在心脑血管病、内科急危重症方面有一定的临床诊疗经验，擅长以心血管病为主，如冠心病、高血压病、高脂血症，动脉粥样硬化，肺心病、心肌梗塞、先心病、心力衰竭，心肌病，心肌炎，心理疾病等。', '南昌大学第一附属医院心血管内科进修一年，心血管介入室半年', '乐平市中医院', 3.7, 0.87, 314, 4);
INSERT INTO `doctor` VALUES (10096, 1, '孔祥照', 'abcd1234', '86.jpg', '脑梗塞', 4, '擅长内科常见疾病的中医特色治疗，尤其擅长心血管系统，呼吸系统及消化系统常见疾病的中西医结合诊治。对中医体质辩识及中医养生有丰富的经验。', '中医学硕士，毕业于广州中医药大学', '广州市中医院', 4.9, 0.95, 486, 4.4);
INSERT INTO `doctor` VALUES (10097, 1, '黄耀忠', 'abcd1234', '90.jpg', '脑梗塞', 2, '擅长头痛、头晕、癫痫、睡眠障碍、痴呆、脑血管疾病、中枢神经系统感染、周围神经疾病、急性脊髓炎、多发性硬化、帕金森病、神经症、周期性瘫痪的诊治。', '毕业于中山医科大学临床医学系临床医学专业，现任广东省老年保健协会神经内科专业委员会常务委员，广东省康复医学会康复治疗专业委员会委员员。', '中信惠州医院', 4.4, 0.9, 964, 4.6);
INSERT INTO `doctor` VALUES (10098, 1, '潘必树', 'abcd1234', '109.jpg', '脑梗塞', 1, '擅长神经内科.急救医学，心血管.呼吸内科.消化内科方面的诊疗，对于儿科常见病也有一定经验。', '大学本科毕业于湖北医药学院！', '兴宁市人民医院', 3.2, 0.83, 697, 4.5);
INSERT INTO `doctor` VALUES (10099, 1, '李隆文', 'abcd1234', '110.jpg', '脑梗塞', 2, '内科常见疾病的诊治，另外兼有对祖国医学的认识，可以指导一定程度的养生保健的方法及人生哲学的认识。擅长神经科疾病的诊治，主攻方向是脑血管疾病的诊治及精神心理疾病的诊治', '中山大学医学院神经内科学硕士研究生，临床经历20余年，临床工作业务及解决问题的能力较强', '广州市萝岗中医医院', 4.6, 0.93, 94, 2.4);
INSERT INTO `doctor` VALUES (10100, 1, '张文举', 'abcd1234', '111.jpg', '脑梗塞', 3, '擅长心血管内科常见疾病的诊断治疗，特别是冠心病，心肌梗塞，急慢性心衰，高血压，糖尿病，心律失常，肺栓塞，病毒性心肌炎的诊治，擅长胸痛的诊断鉴别', '2009年毕业于郑州大学医学院，次年参加工作，于周口市中心医院心血管内科从事医疗工作，2014年调职至郑州人民医院工作至今。河南省中西医结合学会脑心同治分会委员。', '郑州人民医院', 4.8, 0.96, 1346, 4.8);
INSERT INTO `doctor` VALUES (10101, 1, '肖童', 'abcd1234', '112.jpg', '脑梗塞', 2, '擅长冠心病，高血压，心肌病，心肌炎，心律失常，房颤，心衰等心血管疾病，合并糖尿病的心脏病的规范诊治，药物合理化应用。还可提供体检报告专业分析，健康咨询。', '医学临床专业学位硕士，解放军第四军医大学毕业，从事临床工作10余年，曾于中国医学科学院阜外心血管病医院进修1年。', '河南省胸科医院', 4.3, 0.9, 245, 3.1);
INSERT INTO `doctor` VALUES (10102, 1, '胡中辉', 'abcd1234', '113.jpg', '脑梗塞', 2, '脑梗塞，癫痫，帕金森病，椎体外系病变等。', '2011年毕业于西藏大学医学院', '许昌市立医院', 4.3, 0.91, 1345, 4.6);
INSERT INTO `doctor` VALUES (10103, 1, '许彦臣', 'abcd1234', '114.jpg', '脑梗塞', 2, '脑梗塞，脑出血，头痛，头晕，睡眠障碍，焦虑，抑郁，颈肩腰腿痛，风湿及类风湿性关节炎，骨性关节炎，胃炎，心肌炎，冠心病等等。', '本科毕业于黑龙江中医药大学，硕士毕业于中国中医科学院，曾在北京大学人民医院及河南省人民医院进修，在海南三亚中医院工作4年，来河南省中医药研究院附属医院工作1年。', '河南中医药研究院附属医院', 4.5, 0.97, 123, 2.9);
INSERT INTO `doctor` VALUES (10104, 1, '潘国焰', 'abcd1234', '115.jpg', '脑梗塞', 2, '在心内科常见病、多发病、疑难病如急性心肌梗死、急性心力衰竭、恶性心律失常等疾病治疗方面具有丰富的经验，积极从事冠心病的介入治疗，成功抢救多例心脏骤停患者', '硕士研究生，心血管内科主治医师，毕业于福建医科大学，师从我国著名的心血管病专家林荣教授', '莆田市第一医院', 4.6, 0.96, 634, 4.5);
INSERT INTO `doctor` VALUES (10105, 1, '蔡亚飞', 'abcd1234', '116.jpg', '慢性肝炎', 1, '毕业于南阳医学高等专科学校，在许昌市中心医院消化内科，呼吸内科进修一年，后取得郑州大学临床医学系学士学位，擅长急慢性胃炎，胃溃疡，十二指肠溃疡，胃_食管反流症，急慢性胆囊炎，急性胰腺炎，急慢性肝炎，急、慢性肠炎，溃疡性结肠炎等消化内科常见病的诊治！', '毕业于南阳医学高等专科学校，在许昌市中心医院进修一年，已经取得郑州大学临床医学系学士学位。', '许昌市第二人民医院', 3.7, 0.87, 347, 3.6);
INSERT INTO `doctor` VALUES (10106, 1, '张建立', 'abcd1234', '117.jpg', '慢性肝炎', 4, '消化内科硕士研究生毕业，从事临床工作20年余，对胃肠及肝病学的常见、多发情况有独到的见解。尤其是擅长急慢性肝炎，肝硬化，以及各种胃肠道疾病等。', '郑州大学硕士研究生', '郑州市第二人民医院', 4.9, 0.97, 720, 4.6);
INSERT INTO `doctor` VALUES (10107, 1, '周宏华', 'abcd1234', '118.jpg', '慢性肝炎', 2, '擅长消化系统相关性疾病，如急慢性胃炎，胃十二指肠溃疡，胰腺炎，急慢性肝炎，肝硬化等等！', '安徽中医药大学 中医学 本科。 扬州大学医学院 消化内科 研究生。', '天长市人民医院', 4.2, 0.89, 631, 4.5);
INSERT INTO `doctor` VALUES (10108, 2, '廖芳梅', 'abcd1234', '99.jpg', '慢性肝炎', 2, '从事消化内科临床工作29年。擅长消化内科常见病多发病的诊治。比如食管炎胃炎，胃溃疡，12指肠溃疡，慢性溃疡性结肠炎等。慢性胆囊炎，脂肪肝，慢性乙丙型肝炎，肝炎后肝硬化，免疫性肝病。对于一些疑难杂症有一定经验。特别在肝病治疗方面有专长。', '2002年曾在北京解放军302医院和北京地坛医院进修学习。曾发表多篇国家级论文。多次参加全国消化病会议交流。', '凌源分局中心医院', 4.5, 0.94, 840, 4.6);
INSERT INTO `doctor` VALUES (10109, 1, '李巧登', 'abcd1234', '119.jpg', '慢性肝炎', 2, '擅长于肝胆道及胰腺各种疾病的诊断与治疗，包括：①肝脏、胆道肿瘤的诊断与治疗;②肝胆管结石、先天性胆管囊性扩张症的诊断与治疗;③各种胆囊疾病，如胆囊炎，胆囊结石等的诊断和治疗;④肝硬化门脉高压症的治疗', '研究生学历', '芜湖市第二人民医院', 4.6, 0.95, 745, 4.5);
INSERT INTO `doctor` VALUES (10110, 1, '丁江', 'abcd1234', '120.jpg', '慢性肝炎', 2, '掌握消化性溃疡，急慢性胃炎，消化不良，克罗恩，溃结，直肠炎，肛瘘，内外痔，肝胆疾病等内科常见病多发病的诊治。曾进修男性科对包皮包茎及性功能障碍等有较好的认识。', '毕业于江西中医学院，临床医学专业。', '修水县中医院', 4.5, 0.93, 974, 4.6);
INSERT INTO `doctor` VALUES (10111, 1, '曾辉', 'abcd1234', '121.jpg', '慢性肝炎', 2, '消化内科常见疾病', '我毕业于昆明医科大学，有专业的临床知识与经验。', '赣州市市立医院', 4.3, 0.91, 241, 3.1);
INSERT INTO `doctor` VALUES (10112, 1, '陈益忠', 'abcd1234', '122.jpg', '慢性肝炎', 3, '消化系统疾病及内镜治疗', '1992年8月毕业于福建医科大学临床医学系。', '福建省南平市第一医院', 4.7, 0.95, 234, 3);
INSERT INTO `doctor` VALUES (10113, 1, '郑新钻', 'abcd1234', '123.jpg', '慢性肝炎', 1, '消化不良，消化性溃疡，消化道出血，肝脏疾病，便秘与腹泻', '于2005至2010年贵阳医学院学习临床医学，2009年于厦门市第二医院实习，毕业即到医院上班，多次参加学术交流', '泉州市第一医院', 3.8, 0.9, 124, 2.9);
INSERT INTO `doctor` VALUES (10114, 2, '黄安娜', 'abcd1234', '100.jpg', '慢性肝炎', 1, '擅长各种常见消化系统疾病、呼吸道疾病、神经系统疾病、心血管系统疾病、内分泌系统疾病，及各种内科常见病的诊疗。', '毕业于南方医科大学（原第一军大学），从医十余年，有丰富的临床诊疗经验。', '南方医科大学南方医院', 3.9, 0.89, 765, 4.5);
INSERT INTO `doctor` VALUES (10115, 2, '刘莎', 'abcd1234', '101.jpg', '甲亢', 3, '糖尿病、骨质疏松、甲状腺疾病、痛风、肥胖症等内分泌科疾病以及各种疾病的营养治疗、饮食指导等等', '本科专业为临床医学，硕士研究生为内科学，主攻内分泌及代谢性疾病。', '南昌大学第二附属医院', 4.7, 0.95, 268, 3.2);
INSERT INTO `doctor` VALUES (10116, 1, '胡猛龙', 'abcd1234', '124.jpg', '甲亢', 2, '擅长内分泌疾病治疗，尤其是甲状腺疾病，特别是甲状腺疾病、甲亢等垂体引发的老年性内分泌疾病。', '本人临床医院专业毕业，毕业后一直从事于内分泌科工作，并取得内分泌科主治医生，目前对内分泌科的常见病和部分疑难疾病的诊断和治疗有了熟练的掌握，希望借此平台更好的服务于大家。', '江西省长征医院', 4.5, 0.92, 237, 3.6);
INSERT INTO `doctor` VALUES (10117, 1, '陈益忠', 'abcd1234', '125.jpg', '甲亢', 2, '内分泌系统疾病治疗及防治', '1992年8月毕业于福建医科大学临床医学系。', '福建省南平市第一医院', 4.6, 0.93, 127, 3.6);
INSERT INTO `doctor` VALUES (10118, 1, '江小杰', 'abcd1234', '126.jpg', '甲亢', 2, '甲状腺乳腺常见病诊疗。乳腺增生，积乳，乳腺炎，乳腺肿瘤。甲状腺功能异常，甲状腺结节，甲状腺肿瘤。胆囊结石，肝功能异常，胆囊息肉，肝癌，肝囊肿，肝血管瘤。', '博士，外科学专业，主治医师', '莆田学院附属医院', 4.4, 0.89, 947, 4.5);
INSERT INTO `doctor` VALUES (10119, 1, '潘国焰', 'abcd1234', '127.jpg', '甲亢', 1, '擅长甲状腺结节穿刺，甲状腺结节热消微创手术，甲亢，甲状腺囊肿，甲状腺腺瘤。', '硕士研究生，分泌科主治医师，毕业于福建医科大学，师从我国著名的甲亢病专家林荣教授', '莆田市第一医院', 3.7, 0.83, 634, 4.5);
INSERT INTO `doctor` VALUES (10120, 1, '吴清国', 'abcd1234', '128.jpg', '甲亢', 4, '从事内分泌内科13年，从事冠心病、高血压病、甲亢、糖尿病等内分泌疾病治疗。擅长甲亢及甲亢并发症疾病治疗。', '2000至2005就读福建医科大学临床医学专业，毕业后于莆田市第一医院分泌内科工作。', '莆田市第一医院', 4.9, 0.98, 127, 3.5);
INSERT INTO `doctor` VALUES (10121, 1, '范进绵', 'abcd1234', '129.jpg', '甲亢', 3, '对糖尿病、妊娠期糖尿病、甲亢、甲减、妊娠合并甲亢、妊娠合并甲减、甲状腺炎、痛风等疾病有丰富的临床经验。', '2006年毕业于广州医科大学临床专业，从事内分泌科临床工作10余年，2013年至广东省人民医院内分泌科进修学习1年，2016年通过副高职称评审。', '潮阳区大峰医院', 4.8, 0.94, 244, 3);
INSERT INTO `doctor` VALUES (10122, 1, '林涌坚', 'abcd1234', '130.jpg', '甲亢', 2, '擅长甲亢、甲减疾病治疗，特别是老年人甲减。', '曾在南方医科大学附属珠江医院及广东省人民医院进修', '揭阳市人民医院', 4.5, 0.92, 250, 3.1);
INSERT INTO `doctor` VALUES (10123, 2, '朱红艳', 'abcd1234', '102.jpg', '甲亢', 2, '熟知临床医学内科各常见病症（包括消化，呼吸，血液等科疾病），擅长内分泌及代谢性疾病，如糖尿病，甲亢，甲减等。', '本科学习临床医学，硕士研究生专业内科学，主要攻读内分泌及代谢性疾病。', '上饶市人民医院', 4.1, 0.88, 633, 4.5);
INSERT INTO `doctor` VALUES (10124, 1, '李侃', 'abcd1234', '131.jpg', '精神病', 4, '中老年人抑郁症、精神分裂症、失眠症、神经症、焦虑症等。', '从事中老年人精神病临床与研究20余年，具有深厚的专业功底，对患者充满赖心和爱心。', '江西省精神病医院', 4.9, 0.98, 423, 4.1);
INSERT INTO `doctor` VALUES (10125, 1, '陈洪汉', 'abcd1234', '132.jpg', '精神病', 3, '老年性周围神经疾病、抑郁症、焦虑症及身心疾病等神经精神疾病诊治', '曾受教于广州军区武汉总医院神经内科三年，曾在湘雅二医院精神科进修，从事治疗中老年人神经病8年，临床经验丰富。', '黄冈市中心医院', 4.7, 0.94, 1435, 4.9);
INSERT INTO `doctor` VALUES (10126, 2, '骆晓林', 'abcd1234', '103.jpg', '精神病', 2, '各种睡眠障碍、抑郁症、焦虑症、强迫症，特别是中老人群神经衰弱、精神分裂症。', '骆晓林，女，主任医师，湘雅二院及湖南中医药大学兼职教授，对于中老年人神经病治疗准确熟练，并积累了丰富的临产经验。', '湖南省第二人民医院', 4.3, 0.89, 324, 3.5);
INSERT INTO `doctor` VALUES (10127, 1, '王自湘', 'abcd1234', '133.jpg', '精神病', 2, '精神分裂症、强迫症、抑郁症、焦虑症等疾病的治疗，特别擅长老年精神分裂症治疗。', '曾在中南大学湘雅二医院进行职业培训，擅长老年精神分裂症治疗，治疗经验丰富，治疗效果显著。', '平江县第四人民医院', 4.5, 0.92, 98, 2.9);
INSERT INTO `doctor` VALUES (10128, 2, '童艳琼', 'abcd1234', '104.jpg', '精神病', 2, '抑郁症、焦虑症、失眠症、老年性焦虑、神经衰弱、更年期综合征的治疗。', '多次赴全国各地参加各种精神病专业医疗培训，执业经验丰富，病情诊断明确，治疗案例突出。', '鄂钢医院', 4.8, 0.95, 107, 3.1);
